const container=document.querySelector(".categories-container"),getData=async()=>{const a=await fetch("app/assets/data/data.json");return await a.json()},getCategoryClass=a=>{switch(a){case"Reaction":return"category-reaction";case"Memory":return"category-memory";case"Verbal":return"category-verbal";case"Visual":return"category-visual";default:return""}},populate=a=>{a.forEach((a=>{const{category:e,score:c,icon:s}=a,n=document.createElement("div"),t=getCategoryClass(e);n.classList.add("category",`${t}`),n.innerHTML=`\n      <img class="category-icon" src="${s}" alt="${e}">\n      <div class="category-name">${e}</div>\n      <div class="score">\n        <span class="actual-score">${c}</span>\n        <span class="max-score"> / 100</span>\n      </div>\n  `,container.appendChild(n)}))};getData().then((a=>{a.forEach((a=>{const{category:e,score:c,icon:s}=a,n=document.createElement("div"),t=getCategoryClass(e);n.classList.add("category",`${t}`),n.innerHTML=`\n      <img class="category-icon" src="${s}" alt="${e}">\n      <div class="category-name">${e}</div>\n      <div class="score">\n        <span class="actual-score">${c}</span>\n        <span class="max-score"> / 100</span>\n      </div>\n  `,container.appendChild(n)}))})).catch((a=>console.error(a)));