import data from"/app/assets/data/data.json"assert{type:"json"};const container=document.querySelector(".categories-container");function getCategoryClass(a){switch(a){case"Reaction":return"category-reaction";case"Memory":return"category-memory";case"Verbal":return"category-verbal";case"Visual":return"category-visual";default:return""}}data.forEach((a=>{const{category:e,score:t,icon:s}=a,r=document.createElement("div"),c=getCategoryClass(e);r.classList.add("category",`${c}`),r.innerHTML=`\n      <img class="category-icon" src="${s}" alt="${e}">\n      <div class="category-name">${e}</div>\n      <div class="score">\n        <span class="actual-score">${t}</span>\n        <span class="max-score"> / 100</span>\n      </div>\n  `,container.appendChild(r)}));